package week1.Day2.Assignments;

public class MyCalculator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		Calculator obj = new Calculator();

		System.out.println("Addition : " + obj.add(100, 200, 300));
		System.out.println("Subtraction : " + obj.sub(300, 200));
		System.out.println("Multiplication : " + obj.mul(55555555, 55555555));
		System.out.println("Division : " + obj.div(5.5f, 2.5f));
	}

}
